import re; cmake_file = open('tiledb/sm/c_api/tiledb_version.h', 'r'); defines_regex = r'#define\s([^\s]+)\s([^\s]+)'; vars = dict(list(map(lambda defineline: (re.search(defines_regex, defineline).group(1), re.search(defines_regex, defineline).group(2)), filter(lambda line: line.startswith('#define'), cmake_file.read().split('\n'))))); version_major = vars['TILEDB_VERSION_MAJOR']; version_minor = vars['TILEDB_VERSION_MINOR']; version_patch = vars['TILEDB_VERSION_PATCH']; print(f'{version_major}.{version_minor}.{version_patch}')